mapper.select:
/* 혁신 IDEA+ 조회 system.menu.ideaUs.selectList */ SELECT A.IDEA_CD, A.YEAR, A.USER_ID, CA.CODE_NM AS CATEGORY, A.TITLE, A.CONTENT, A.STATE,
TO_CHAR(A.CREATE_DT, 'YYYY.MM.DD') AS CREATE_DT, A.UPDATE_DT, A.DELETE_DT, A.START_DT, A.END_DT, A.ATCH_FILE_ID, A.IDEA_GBN_CD, A.DEGREE, A.EVAL_STATE, B.USER_NM, B.DEPT_NM FROM IDEA_INFO A LEFT JOIN V_ROLE_USER B ON A.USER_ID = B.USER_ID
WHERE A.IDEA_GBN_CD ='002'
AND A.DELETE_DT IS NULL
AND A.DELETE_DT IS NOT NULL
AND UPPER(A.TITLE) LIKE '%' || TRIM(UPPER(#{searchKeyword})) ||'%'
AND UPPER(B.USER_NM) LIKE '%' || TRIM(UPPER(#{searchKeyword})) ||'%'
AND A.YEAR = #{findYear}
AND A.CATEGORY = #{findCategory}
ORDER BY A.CREATE_DT DESC

mapper.update:
/*	혁신 IDEA+ 삭제	system.menu.ideaUs.deleteIdeaUs	*/
UPDATE IDEA_INFO A
SET
A.DELETE_DT = SYSDATE
WHERE 1=1
AND A.IDEA_CD IN

	#{key}

AND A.YEAR=#{findYear}
